import React, { useState } from "react";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";
import Axios from "axios";
let DummyExpenses = [];
Axios.post("http://localhost:3000/getAllExpenses")
  .then((response) => {
    DummyExpenses = response.data;
  })
  .catch((error) => {
    console.error(error);
  });

function App() {

  const [expenses, setExpenses] = useState(DummyExpenses);


  const newExpwnseHandler = (expense) => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };

  const setExpensesHandler = () => {
    console.log('123');
    setExpenses(DummyExpenses)
  }


  return (
    <div>
      <NewExpense onNewExpense={newExpwnseHandler} />
      <Expenses items={expenses} setExpenses={setExpensesHandler}/>
    </div>
  );
}

export default App;
